INSERT INTO content_of_pages (title, description, image_link, type, creating_date, creating_time) VALUES ('13 апреля 2014 года состоялся Ломоносовский турнир среди школьников', '<div><h6 class="media-text news-full-text" id="post_date">13.04.2014</h6><h4 class="media-text news-full-text" id="post_text">Ломоносовский турнир – это традиционное мероприятие для школьников, проводимое ОмГУ им. Ф.М. Достоевского. Первый турнир им. М.В. Ломоносова состоялся в феврале 2000 г. В нём приняло участие около 800 омских школьников. С тех пор турнир получил широкую известность не только в омском регионе, но и далеко за его пределами. Только в прошлом учебном году в нём приняли участие 18 067 школьников из 557 общеобразовательных учреждений 47 регионов России и Республики Казахстан. Более тысячи школьников стали участниками финальной части турнира, 342 из них награждены дипломами призёров и похвальными грамотами, а 19 школьников стали обладателями главной награды – звания «Юный Ломоносов».<p></p><p>Ломоносовский турнир – это интеллектуальное соревнование, рассчитанное на участие в нём школьников без предварительного отбора.</p><p></p><p>По итогам турнира каждый участник получает сертификат участника, победители и призёры в каждой предметной секции - дипломы и памятные подарки. Те же школьники, которые заняли призовые места в нескольких секциях становятся обладателями высшего достижения на турнире – специального диплома с присвоением почетного звания «Юный Ломоносов».</p></h4></div><p></p><div class="center" style="overflow: hidden;"><div class="news-full-image-container"><a target="_blank" href="http://cs616419.vk.me/v616419575/a330/V92h7RM4fHc.jpg"><img class="news-full-image" src="http://cs616419.vk.me/v616419575/a330/V92h7RM4fHc.jpg"></a></div><div class="news-full-image-container"><a target="_blank" href="http://cs616419.vk.me/v616419575/a339/WtudoeA73Tw.jpg"><img class="news-full-image" src="http://cs616419.vk.me/v616419575/a339/WtudoeA73Tw.jpg"></a></div><div class="news-full-image-container"><a target="_blank" href="http://cs616419.vk.me/v616419575/a3c0/xO-FYTwzvQQ.jpg"><img class="news-full-image" src="http://cs616419.vk.me/v616419575/a3c0/xO-FYTwzvQQ.jpg"></a></div></div>', 'http://cs622829.vk.me/v622829584/118a5/J5JSiHfTn2U.jpg', 'News:Social', 'today', 'now');
INSERT INTO content_of_pages (title, description, image_link, type, creating_date, creating_time) VALUES ('26 января 2014 года состоялся ректорский бал', '<div><h6 class="media-text news-full-text" id="post_date">27.01.2014</h6><h4 class="media-text news-full-text" id="post_text">26 января 2014 г.- одно из самых красивых событий для студентов ОмГУ. Его ждут, к нему готовятся. Ректорский бал - бал для самых-самых…</h4></div><p></p><div class="center" style="overflow: hidden;"><div class="news-full-image-container"><a target="_blank" href="http://cs622829.vk.me/v622829584/118a5/J5JSiHfTn2U.jpg"><img class="news-full-image" src="http://cs622829.vk.me/v622829584/118a5/J5JSiHfTn2U.jpg"></a></div><div class="news-full-image-container"><a target="_blank" href="http://cs624520.vk.me/v624520413/199b5/Ulyg-wYzPwI.jpg"><img class="news-full-image" src="http://cs624520.vk.me/v624520413/199b5/Ulyg-wYzPwI.jpg"></a></div><div class="news-full-image-container"><a target="_blank" href="http://cs624520.vk.me/v624520413/196f4/FzzCHAvvePg.jpg"><img class="news-full-image" src="http://cs624520.vk.me/v624520413/196f4/FzzCHAvvePg.jpg"></a></div><div class="news-full-image-container"><a target="_blank" href="http://cs624520.vk.me/v624520413/18b32/WwFrZ9nBksA.jpg"><img class="news-full-image" src="http://cs624520.vk.me/v624520413/18b32/WwFrZ9nBksA.jpg"></a></div></div>', 'http://cs624520.vk.me/v624520413/199b5/Ulyg-wYzPwI.jpg', 'News:Social', 'today', 'now');